import { createStore } from 'vuex'
import user from './modules/user'
import app from './modules/app'

export default createStore({
  modules: {
    user,
    app
  },
  state: {
    // 全局加载状态
    loading: false,
    
    // 系统信息
    systemInfo: {
      version: 'v1.0.0',
      name: '充电系统'
    }
  },
  mutations: {
    // 设置加载状态
    SET_LOADING(state, status) {
      state.loading = status
    },
    
    // 更新系统信息
    UPDATE_SYSTEM_INFO(state, info) {
      state.systemInfo = {
        ...state.systemInfo,
        ...info
      }
    }
  },
  actions: {
    // 显示全局加载
    showLoading({ commit }) {
      commit('SET_LOADING', true)
    },
    
    // 隐藏全局加载
    hideLoading({ commit }) {
      commit('SET_LOADING', false)
    },
    
    // 获取系统信息
    getSystemInfo({ commit }) {
      // 这里可以调用API获取系统信息
      // 暂时使用静态数据
      const systemInfo = {
        version: 'v1.0.0',
        name: '充电系统',
        buildTime: new Date().toISOString()
      }
      
      commit('UPDATE_SYSTEM_INFO', systemInfo)
    }
  },
  getters: {
    // 获取加载状态
    loading: state => state.loading,
    
    // 获取系统信息
    systemInfo: state => state.systemInfo,
    
    // 获取系统版本
    version: state => state.systemInfo.version,
    
    // 获取系统名称
    systemName: state => state.systemInfo.name,
    
    // 获取用户token
    token: state => state.user.token
  }
})
