import request from '@/utils/request';

const API_URL = process.env.VUE_APP_API_URL || 'http://localhost:5000';

const dashboardApi = {
  /**
   * 获取仪表盘概览数据
   */
  getDashboardOverview() {
    return request({
      url: '/api/system/dashboard',
      method: 'get'
    });
  },
  
  /**
   * 获取系统告警数据
   * @param {Number} page 页码，默认为1
   * @param {Number} perPage 每页数量，默认为7
   */
  getSystemAlerts(page = 1, perPage = 7) {
    return request({
      url: '/api/system/alerts',
      method: 'get',
      params: {
        page,
        per_page: perPage
      }
    });
  },

  /**
   * 获取充电效率趋势数据
   * @param {Object} params - 请求参数
   * @param {number} params.days - 查询天数
   * @returns {Promise} - 返回充电效率趋势数据
   */
  getChargingEfficiencyTrend(params) {
    return request({
      url: '/api/dashboard/charging-efficiency-trend',
      method: 'get',
      params
    });
  },
  
  /**
   * 获取系统概览数据
   * @returns {Promise} - 返回系统概览数据
   */
  getSystemOverview() {
    return request({
      url: '/api/dashboard/system-overview',
      method: 'get'
    });
  },
  
  /**
   * 获取充电站状态统计
   * @returns {Promise} - 返回充电站状态统计数据
   */
  getStationStatusStats() {
    return request({
      url: '/api/dashboard/station-status',
      method: 'get'
    });
  },
  
  /**
   * 获取今日充电订单统计
   * @returns {Promise} - 返回今日充电订单统计数据
   */
  getTodayOrderStats() {
    return request({
      url: '/api/dashboard/today-orders',
      method: 'get'
    });
  }
};

export default dashboardApi;
