import axios from 'axios'

const api = axios.create({
  baseURL: '/api',
  timeout: 5000
})

// 请求拦截器
api.interceptors.request.use(
  config => {
    console.log(`API请求: ${config.method.toUpperCase()} ${config.url}`)
    const token = localStorage.getItem('token')
    if (token) {
      config.headers.Authorization = `Bearer ${token}`
      console.log('已添加认证令牌')
    } else {
      console.log('未找到认证令牌')
    }
    return config
  },
  error => {
    console.error('请求拦截器错误:', error)
    return Promise.reject(error)
  }
)

// 响应拦截器
api.interceptors.response.use(
  response => {
    console.log(`API响应: ${response.config.method.toUpperCase()} ${response.config.url}`, response.data)
    return response
  },
  error => {
    console.error('API错误:', error.response ? error.response.data : error.message)
    return Promise.reject(error)
  }
)

// 充电站相关 API
export const stationApi = {
  // 获取所有充电站
  getAll() {
    console.log('调用API: 获取所有充电站')
    return api.get('/stations')
  },
  // 获取单个充电站
  getById(id) {
    return api.get(`/stations/${id}`)
  },
  // 更新充电站
  update(id, data) {
    return api.put(`/stations/${id}`, data)
  },
  // 添加充电站
  add(data) {
    return api.post('/stations', data)
  },
  // 删除充电站
  delete(id) {
    return api.delete(`/stations/${id}`)
  }
}

// 机器人相关 API
export const robotApi = {
  // 获取所有机器人
  getAll() {
    console.log('调用API: 获取所有机器人')
    return api.get('/robots')
      .then(response => {
        console.log('机器人API响应状态:', response.status)
        console.log('机器人API响应数据类型:', typeof response.data, Array.isArray(response.data))
        
        // 检查响应数据是否为null或undefined
        if (response.data === null || response.data === undefined) {
          console.error('API返回的机器人数据为null或undefined，强制转换为空数组')
          response.data = []
          return response
        }
        
        // 确保返回的数据是数组
        if (!Array.isArray(response.data)) {
          console.error('API返回的机器人数据不是数组，强制转换为空数组')
          
          // 如果数据是对象且有data属性是数组，则使用该属性
          if (response.data && typeof response.data === 'object' && Array.isArray(response.data.data)) {
            console.log('使用响应对象中的data属性作为数据数组')
            response.data = response.data.data
          } else {
            response.data = []
          }
        }
        
        // 确保数组中的每个元素都是有效的机器人对象
        if (Array.isArray(response.data)) {
          response.data = response.data.filter(item => {
            return item && typeof item === 'object' && 'id' in item && 'name' in item
          })
          console.log(`过滤后的机器人数据数量: ${response.data.length}`)
        }
        
        return response
      })
      .catch(error => {
        console.error('获取机器人数据出错:', error)
        // 创建一个包含空数组的响应对象
        return { data: [] }
      })
  },
  // 获取单个机器人
  getById(id) {
    return api.get(`/robots/${id}`)
  },
  // 更新机器人
  update(id, data) {
    return api.put(`/robots/${id}`, data)
  },
  // 添加机器人
  add(data) {
    return api.post('/robots', data)
  },
  // 删除机器人
  delete(id) {
    return api.delete(`/robots/${id}`)
  }
}

// 订单相关 API
export const orderApi = {
  // 获取所有订单
  getAll() {
    console.log('调用API: 获取所有订单')
    return api.get('/orders')
  },
  // 获取单个订单
  getById(id) {
    return api.get(`/orders/${id}`)
  },
  // 创建订单
  create(data) {
    return api.post('/orders', data)
  },
  // 更新订单状态
  updateStatus(id, status) {
    return api.put(`/orders/${id}/status`, { status })
  }
}

// 系统相关 API
export const systemApi = {
  // 获取系统设置
  getSettings() {
    return api.get('/system/settings')
  },
  // 更新系统设置
  updateSettings(data) {
    return api.put('/system/settings', data)
  },
  // 获取仪表盘数据
  getDashboardData() {
    console.log('调用API: 获取仪表盘数据')
    return api.get('/system/dashboard')
  },
  // 获取系统告警
  getAlerts() {
    return api.get('/system/alerts')
  },
  // 获取系统日志
  getLogs() {
    return api.get('/system/logs')
  },
  // 获取充电效率记录
  getEfficiencyLogs() {
    return api.get('/system/efficiency')
  }
}

// 测试API连接
export const testApi = {
  checkData() {
    console.log('调用API: 测试数据加载')
    return api.get('/auth/test-data')
  }
}

export default api
